namespace MyMangaList.Test.Services
{
    using AutoMapper;
    using Microsoft.VisualStudio.TestTools.UnitTesting;
    using MyMangaList.Data;
    using MyMangaList.DtoModels.BindingModels;
    using MyMangaList.DtoModels.ViewModels;
    using MyMangaList.Models;
    using MyMangaList.Services.Users;
    using MyMangaList.Test.Mocks;
    using System;
    using System.Linq;

    [TestClass]
    public class MangaServiceTests
    {
        private MyMangaListContext dbContext;
        private IMapper mapper;

        [TestMethod]
        public void AddManga()
        {
            var user1 = new User() { Id = "1", UserName = "user1", Email = "email@abv.bg" };
            var story = @"Contrary to popular belief, Lorem Ipsum is not simply random text. It has roots in a piece of classical Latin literature from 45 BC, making it over 2000 years old. Richard McClintock, a Latin professor at Hampden-Sydney College in Virginia, looked up one of the more obscure Latin words, consectetur, from a Lorem Ipsum passage, and going through the cites of the word in classical literature, discovered the undoubtable source. Lorem Ipsum comes from sections 1.10.32 and 1.10.33 of de Finibus Bonorum et Malorum (The Extremes of Good and Evil) by Cicero, written in 45 BC. This book is a treatise on the theory of ethics, very popular during the Renaissance. The first line of Lorem Ipsum, Lorem ipsum dolor sit amet.., comes from a line in section 1.10.32.";
            this.dbContext.Users.Add(user1);
            this.dbContext.SaveChanges();

            var bindingModel1 = new MangaBindingModel()
            {
                Title = "Title",
                ShortDescription = "Short Desctription",
                Image = "data:image/jpeg;base64,",
                Genre = "Genre",
                Story = story
            };

            var bindingModel2 = new MangaBindingModel()
            {
                Title = "Title",
                ShortDescription = "Short Desctription",
                Image = "data:image/jpeg;base64,",
                Genre = "Genre",
                Story = story
            };

            var mangaService = new MangaService(dbContext, mapper);

            mangaService.AddManga(user1.UserName, bindingModel1);
            mangaService.AddManga(user1.UserName, bindingModel2);

            Assert.AreEqual(2, this.dbContext.Manga.ToList().Count);
        }

        [TestMethod]
        public void GetFavouriteManga()
        {
            User user1 = AddToDatabase();

            var mangaService = new MangaService(dbContext, mapper);
            var models = mangaService.GetFavouriteManga(user1.Id);

            Assert.AreEqual(2, models.Count());

        }

        [TestMethod]
        public void FavourManga_WithExistingFavour_ReturnNoChange()
        {
            User user = AddToDatabase();

            var manga1 = this.dbContext.Manga.FirstOrDefault(m => m.Id == 1);

            var mangaService = new MangaService(dbContext, mapper);
            mangaService.FavourManga(user.Id, manga1.Id);

            var favouritesCount = this.dbContext.UsersManga.ToList().Count;

            Assert.AreEqual(2, favouritesCount);
        }

        [TestMethod]
        public void UnFavourManga()
        {
            var user = AddToDatabase();
            var manga = this.dbContext.Manga.FirstOrDefault(m => m.Id == 1);

            var mangaService = new MangaService(dbContext,mapper);

            mangaService.UnFavourManga(user.Id, manga.Id);

            Assert.AreEqual(1, this.dbContext.UsersManga.ToList().Count);
        }

        private User AddToDatabase()
        {
            var user1 = new User() { Id = "1", UserName = "user1", Email = "email@abv.bg" };
            var story = @"Contrary to popular belief, Lorem Ipsum is not simply random text. It has roots in a piece of classical Latin literature from 45 BC, making it over 2000 years old. Richard McClintock, a Latin professor at Hampden-Sydney College in Virginia, looked up one of the more obscure Latin words, consectetur, from a Lorem Ipsum passage, and going through the cites of the word in classical literature, discovered the undoubtable source. Lorem Ipsum comes from sections 1.10.32 and 1.10.33 of de Finibus Bonorum et Malorum (The Extremes of Good and Evil) by Cicero, written in 45 BC. This book is a treatise on the theory of ethics, very popular during the Renaissance. The first line of Lorem Ipsum, Lorem ipsum dolor sit amet.., comes from a line in section 1.10.32.";

            var manga1 = new Manga()
            {
                Title = "Title",
                ShortDescription = "Short Desctription",
                Image = "data:image/jpeg;base64,",
                Genre = "Genre",
                Story = story
            };

            var manga2 = new Manga()
            {
                Title = "Title",
                ShortDescription = "Short Desctription",
                Image = "data:image/jpeg;base64,",
                Genre = "Genre",
                Story = story
            };

            var mangaUser1 = new UserManga() { Id = 1, User = user1, Manga = manga1 };
            var mangaUser2 = new UserManga() { Id = 2, User = user1, Manga = manga2 };

            this.dbContext.UsersManga.Add(mangaUser1);
            this.dbContext.UsersManga.Add(mangaUser2);
            this.dbContext.SaveChanges();
            return user1;
        }
        
        [TestInitialize]
        public void InicializeTests()
        {
            this.dbContext = MockDbContext.GetDbContext();
            this.mapper = MockAutoMapper.GetMapper();
        }
    }

}
